import React, { Component }from "react";
import { nanoid } from "nanoid";
class Header extends Component{
  state={name:""}
  inputCode =(event)=> {
    // console.log(event.target.value)
    if(event.keyCode !==13)
    return
    // console.log(event.target.value)
    if (event.target.value===""){
      alert("输入不能为空")
      return
    }
    const listObj={id:nanoid(),name:event.target.value,done:false}
    this.props.addToDoList(listObj)
    event.target.value=""
  }

  Abcd=(event)=>{
    this.setState({
      name:event.target.value
  })
 }
  getInput=()=>{
    const listObj={id:nanoid(),name:this.state.name,done:false}
    this.props.addToDoList(listObj)
    this.setState({
      name:""
  })
      } 


  render(){

    return(

      <div>
        <input onKeyUp={this.inputCode} onChange={this.Abcd} type="text" placeholder="请输入计划，按回车或确定键确认"></input>
        <button name="确定" onClick={this.getInput}>确定</button>
      </div>
    )
  }
}
export default Header;
