import React, { Component }from "react";
import { nanoid } from "nanoid";
class Header extends Component{
  inputCode =(event)=> {
    // console.log(event.target.value)
    if(event.keyCode !==13)
    return
    // console.log(event.target.value)
    if (event.target.value===""){
      alert("输入不能为空")
      return
    }
    const listObj={id:nanoid(),name:event.target.value,done:false}
    this.props.addToDoList(listObj)
    event.target.value=""
  }
  confirmList=(event)=>{
   if (event.target.value===""){
    alert("输入不能为空")
  }
  const listObj={id:nanoid(),name:event.target.value,done:false}
  this.props.addToDoList(listObj)
  event.target.value=""
  }
  render(){

    return(

      <div>
        <input onKeyUp={this.inputCode} type="text" placeholder="请输入计划"></input>
        <button onClick={this.confirmList} name="确定">确定</button>
      </div>
    )
  }
}
export default Header;
