import React, { Component }from "react";
import Footer from "./component/Footer";
import Header from "./component/Header";
import List from "./component/List";
class App extends Component{
  state={todolist:[
    {id:"1",name:"吃饭",done:true},
    {id:"2",name:"睡觉",done:true},
    {id:"3",name:"玩游戏",done:true},
    {id:"4",name:"代码",done:false}
  ]}
  addToDoList=(listObj)=>{
    const {todolist} =this.state
    const newtodolist=[listObj,...todolist]
    this.setState({todolist:newtodolist})
  }
  updateTodo=(id,done)=>{
     const {todolist}=this.state
    //  const newtodolist=[...todolist,done]
    const newtodolist=todolist.map((listObj)=>{
      if(todolist.id===id) return {...todolist,done}
      else return todolist
    })
    this.setState({todolist:newtodolist})
  }
  render(){
    const {todolist}=this.state
    return(
      <div>
       <Header addToDoList={this.addToDoList}/>
       <List todolist={todolist}/>
       <Footer/>
      </div>
    )
  }
}
export default App;
