import { useState } from "react";

function App() {
  const [pre,setState]=useState(0)
  function  addNum() {
   return setState(pre+1);
  }
  function  deNum() {
    return setState(pre-1);
  }
  return (
    <div>
     <p>{pre}</p>
     <button onChange={addNum}>增加</button>
     <button onChange={deNum}>减少</button>
     <button>间隔增加</button>
    </div>
  );
}

export default App;
