    setDate(e.target.value)
   }
   const [time,setTime] =useState("")
 const changeTime=(e)=>{
  setTime(e.target.value)
 }
 const addContent=()=>{
  const addContent=[date,time,content]
  add(addContent)

 }
    return (
      <div>
        <p>日期</p>
        <input type="date" value={date} onChange={changeDate}/>
        <p>时间</p>
        <input type="time" value={time} onChange={changeTime}/>
        <p>记事</p>
        <input type="text" value={content} onChange={changeContent}/>
        <button className="add" onClick={addContent}>新增</button>
      </div>
    );
  }

